// beautify the login component write better and creative
// add a link to register page
// add a link to forgot password page
// add a link to home page
// add a link to about page
import React from 'react';
import styled from 'styled-components';

import AuthContext from '../context/AuthContext'


const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 300px;
  margin: 0 auto;
  padding: 20px;
  background-color: rgba(255, 255, 255, 0.8);
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const InputField = styled.input`
  width: 100%;
  padding: 10px;
  margin: 5px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
  &:focus {
    outline: none;
    border-color: #007bff;
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 10px;
  background-color: #007bff;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  &:hover {
    background-color: #0056b3;
  }
`;

const LoginForm = ({ loginUser }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const username = formData.get('username');
    const password = formData.get('password');
    loginUser({ username, password });
  };

  return (
    <FormContainer>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <InputField type="text" name="username" placeholder="Enter username" />
        <InputField type="password" name="password" placeholder="Enter password" />
        <SubmitButton type="submit">Login</SubmitButton>
      </form>
    </FormContainer>
  );
};

const LoginPage = () => {


    let {loginUser} = useContext(AuthContext)

    return (
        <div>
            <h1>Login Page</h1>
            <LoginForm loginUser={loginUser}/>
        </div>
    )
}

export default LoginPage
