import React, { useContext } from 'react';
import { Link } from 'react-router-dom'; // Assuming you're using React Router
import styled from 'styled-components';
import AuthContext from '../context/AuthContext';

// Define your styled components for styling
const LoginPageContainer = styled.div`
  background-image: url('your-background-image.jpg'); // Replace with your image URL
  background-size: cover;
  background-position: center;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const LoginForm = styled.form`
  background-color: rgba(255, 255, 255, 0.8);
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1); /* Optional box shadow */
  width: 300px; /* Optional fixed width */
`;

const InputField = styled.input`
  width: 100%;
  padding: 10px;
  margin-bottom: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  &:focus {
    outline: none;
    border-color: #007bff; /* Optional focus style */
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 10px;
  background-color: #007bff; /* Optional button color */
  color: #fff; /* Optional button text color */
  border: none;
  border-radius: 5px;
  cursor: pointer;
  &:hover {
    background-color: #0056b3; /* Optional hover color */
  }
`;

const StyledLink = styled(Link)`
  text-decoration: none;
  color: #007bff;
  margin-right: 10px;
`;

const LoginPage = () => {
  const { loginUser } = useContext(AuthContext);

  const handleLogin = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const username = formData.get('username');
    const password = formData.get('password');
    loginUser({ username, password });
  };

  return (
    <LoginPageContainer>
      <LoginForm onSubmit={handleLogin}>
        <InputField type="text" name="username" placeholder="Enter username" />
        <InputField type="password" name="password" placeholder="Enter password" />
        <SubmitButton type="submit">Login</SubmitButton>
      </LoginForm>
      <div>
        <StyledLink to="/register">Register</StyledLink>
        <StyledLink to="/forgot-password">Forgot Password</StyledLink>
        <StyledLink to="/home">Home</StyledLink>
        <StyledLink to="/about">About</StyledLink>
      </div>
    </LoginPageContainer>
  );
};

export default LoginPage;
